// src/Card.ts
var CardSuit = {
  Hearts: "h",
  Diamonds: "d",
  Clubs: "c",
  Spades: "s"
};
var suitEmojiMap = {
  h: "\u2665\uFE0F",
  d: "\u2666\uFE0F",
  c: "\u2663\uFE0F",
  s: "\u2660\uFE0F"
};
function cardToEmojiString(card) {
  return `${card.rank === "T" ? "10" : card.rank}${suitEmojiMap[card.suit]}`;
}
function cardToString(card) {
  return `${card.rank}${card.suit}`;
}
var CardRank = {
  Two: 2,
  Three: 3,
  Four: 4,
  Five: 5,
  Six: 6,
  Seven: 7,
  Eight: 8,
  Nine: 9,
  Ten: "T",
  Jack: "J",
  Queen: "Q",
  King: "K",
  Ace: "A"
};

export {
  CardSuit,
  cardToEmojiString,
  cardToString,
  CardRank
};
